def add(x, y):
    return x + y

def subtract(x, y):
    return x - y

def multiply(x, y):
    return x * y

def divide(x, y):
    return x / y

def pow(x, y):
    return x ** y

def intDivide(x, y):
    return x // y

def leftShift(x, y):
    return x << y

def rightShift(x, y):
    return x >> y